mapper.update:
update com_board b
set b.count = (select count(rno) from com_reply where bno = #{bno})
where b.bno = #{bno};

update com_board
set cnt=cnt+1
where bno = #{bno}

update com_board
set title=#{title},
content=#{content}
where bno=#{bno}

mapper.select:
select *
from(
	select @rownum:=@rownum+1 rn, a.*
    from notice a, (select @rownum:=0) as sr
    order by pin desc, bno desc
    )as noticee
    
    where rn>(#{pageNum}-1)*#{amount} and rn<=#{pageNum}*#{amount}

mapper.update:
update notice
set title=#{title},
content=#{content}
where bno=#{bno}
