mapper.select:
  		select * from member
  		where id=#{id} 
		and password=#{password}

mapper.update:
update member 
set password=#{password}, 
email=#{email},
name=#{name}
where id=#{id};

mapper.select:
  	select id from member
  		where name=#{name} 
		and number=#{number}
		and email=#{email}

  	select password from member
  		where  name=#{name}
   		and id=#{id} 
		and number=#{number}
		and email=#{email}
