mapper.select:
select*
from (
	select @rownum:=@rownum+1 num,b.*
	from service_list 	b ,(select @rownum:=0 )  as tmp
	where bgno=#{bgno}
	
		
			
				and title like concat('%',#{keyword},'%')
			
			
				and content like concat('%',#{keyword},'%')
			
			
				OR title like concat('%',null,'%')
				OR content like concat('%',null,'%')
			
		
	
	order by bno desc
	)as bkind
	
		where num> (#{pageNum}-1)*#{amount} and num<=#{pageNum}*#{amount}

mapper.update:
update service_list
set cnt=cnt+1
where bno = ${bno}

   update service_list
	   set title=#{title},
		   content=#{content}
   where bno=#{bno}
