mapper.select:
SELECT rownum,bno,w_name,w_price,w_regdata,w_color,w_type,w_quantity, Concat(Replace(p_upload, '\\','/'),'/s_' ,p_uid,'_' ,p_name)AS file FROM (SELECT @rownum:=@rownum+1 rownum, t.* FROM (SELECT *FROM (SELECT p_uid,p_upload,p_name,p_image, w.bno,w_name,w_price,w_regdata,w_color,w_type,w_quantity FROM writing w JOIN product p ON w.bno = p.bno WHERE p_image = 1 )AS j GROUP BY bno
where w_name like concat('%',#{keyword},'%')
where w_type like concat('%',#{keyword},'%')
where w_name like concat('%',#{keyword},'%') or w_type like concat('%',#{keyword},'%')
) AS t, (SELECT @rownum:=0) AS tmp ORDER BY bno DESC) AS balist
where rownum > (#{pageNum}-1)*#{amount} and rownum <= #{pageNum}*#{amount}

select*from writing w join
product p on w.bno = p.bno where
w.bno=#{bno};
