mapper.select:
select * from shop_item i join
writing w on i.bno= w.bno where i.bno =
#{bno}

select rownum,bno,w_name,w_price,w_regdata,w_quantity,w_type,vision,Concat(Replace(p_upload, '\\','/'),'/' ,p_uid,'_' ,p_name)AS filem from( select @rownum:=@rownum+1 rownum, a.* from( select * from( select w.bno,w_name,w_price,w_regdata,w_quantity,w_type,vision,p_upload,p_uid,p_name from writing w join product p on w.bno = p.bno where w_type=#{w_type} and p_image = 1 and vision = 'm')as j) as a,( select @rownum:=0)as tmp
ORDER BY bno desc
ORDER BY w_price
ORDER BY w_price desc
) as balist;

select rownum,bno,w_name,w_price,w_regdata,w_quantity,w_type,s_type,vision,Concat(Replace(p_upload,'\\','/'),'/',p_uid,'_' ,p_name)AS filem from(select @rownum:=@rownum+1 rownum, a.*from( select * from(select w.bno,w_name,w_price,w_regdata,w_quantity,w_type,s_type,vision,p_upload,p_uid,p_name from writing w join product p on w.bno = p.bno where p_image = 1 and vision = 'm')as j) as a,(select @rownum:=0)as tmp
ORDER BY bno desc
ORDER BY w_price
ORDER BY w_price desc
) as balist;
